function parseRequest(str) {
  const lines = str.split(/\r?\n/);
  const [method, path, version] = lines.shift().split(" ");
  const headers = {};
  while(lines.length > 0) {
    const line = lines.shift();
    if(line === "") {
      break;
    }
    const parts = line.split(":");
    const key = parts.shift().toLowerCase();
    const value = parts.join(":");
    headers[key] = value.trim();
  }
  const body = lines.join("\r\n");
  return {
    method: method.toUpperCase(),
    path,
    version,
    headers,
    body
  };
}

function parseFormData(str) {
  const params = str.split("&");
  const formData = {};
  for(const param of params) {
    const parts = param.split("=");
    const key = parts.shift();
    const value = parts.join("=");
    formData[key] = value;
  }
  return formData;
}

function encodeHTML(str) {
  let result = "";
  for(const char of str) {
    result += `&#${char.charCodeAt()};`;
  }
  return result;
}

const HTML_TEMPLATE = `<!-- Generated by Generate CSRF PoC workflow of Caido: https://github.com/caido/workflows/tree/main/convert/Generate%20CSRF%20PoC -->
<html>
	<head>
		<title>CSRF PoC</title>
	</head>
	<body>
		%FORM%
		<script>
			document.querySelector("form").submit();
		</script>
	</body>
</html>
`;

const FORM_TEMPLATE = `<form action="%URL%" method="%METHOD%" enctype="%ENCTYPE%">\n%FIELDS%\t\t</form>`;
const FIELD_TEMPLATE = `\t\t\t<input name="%NAME%" value="%VALUE%">\n`;

function generateFormDataPoC(request) {
  const formData = parseFormData(request.body);
  let fields = "";
  for(const key of Object.keys(formData)) {
    fields += FIELD_TEMPLATE.replace("%NAME%", encodeHTML(key)).replace("%VALUE%", encodeHTML(formData[key]));
  }

  const form = FORM_TEMPLATE.replace("%URL%", encodeHTML(`https://${request.headers["host"]+request["path"]}`)).replace("%METHOD%", request.method).replace("%ENCTYPE%", "application/x-www-form-urlencoded").replace("%FIELDS%", fields);
  return HTML_TEMPLATE.replace("%FORM%", form);
}

function generateJSONPoC(request) {
  const body = request.body.replace(/}\s*$/, "") + ', "';
  let fields = FIELD_TEMPLATE.replace("%NAME%", encodeHTML(body)).replace("%VALUE%", encodeHTML('": ""}'));

  const form = FORM_TEMPLATE.replace("%URL%", encodeHTML(`https://${request.headers["host"]+request["path"]}`)).replace("%METHOD%", request.method).replace("%ENCTYPE%", "text/plain").replace("%FIELDS%", fields);
  return HTML_TEMPLATE.replace("%FORM%", form);
}


export function run(input, sdk) {
  let parsed = sdk.asString(input);
  const request = parseRequest(parsed);
  if(!["GET", "POST"].includes(request.method)) {
    return `Unexpected method: ${request.method}`;
  }

  if(!request.headers["host"]) {
    return `Missing Host header: ${parsed}`;
  }

  const contentType = request.headers["content-type"].split(";")[0];
  switch(contentType) {
    case "application/x-www-form-urlencoded":
      return generateFormDataPoC(request);
      break;
    case "application/json":
      return generateJSONPoC(request);
      break;
    default:
      return `Unexpected Content-Type: ${contentType}. Currently, only application/x-www-form-urlencoded and application/json are supported.`;
  }
}
